-- default expressions
CREATE TABLE DEFAULT_EXPRESSION_SHORT (
    ID  INT PRIMARY KEY,
    STR VARCHAR2(8) DEFAULT 'even'
);

CREATE TABLE DEFAULT_EXPRESSION_4K (
    ID  INT PRIMARY KEY,
    STR VARCHAR2(4000) DEFAULT ''
);

CREATE TABLE DEFAULT_EXPRESSION_4001 (
    ID   INT PRIMARY KEY,
    STR2 CLOB,
    STR  CLOB
);

ALTER TABLE DEFAULT_EXPRESSION_4001
    MODIFY STR DEFAULT LPAD('a', 32767, 'a');

-- check constraints predicates
CREATE TABLE CHECK_CONSTR_SMALL (
    MAKER     VARCHAR(10) NOT NULL,
    MODEL     VARCHAR(50) PRIMARY KEY,
    TYPE      VARCHAR(50) NOT NULL,
    AVAILABLE VARCHAR(20) DEFAULT 'Yes',
    CHECK (AVAILABLE IN ('Yes', 'No'))
);

CREATE TABLE CHECK_CONSTR_4001 (
    MAKER     VARCHAR(10) NOT NULL,
    MODEL     VARCHAR(50) PRIMARY KEY,
    TYPE      VARCHAR(50) NOT NULL,
    AVAILABLE VARCHAR(20) DEFAULT 'Yes',
    CHECK (AVAILABLE IN ('Yes', 'No', 'Middle',
                         ''))
);

CREATE TABLE CHECK_CONSTR_4001 (
    MAKER     VARCHAR(10) NOT NULL,
    MODEL     VARCHAR(50) PRIMARY KEY,
    TYPE      VARCHAR(50) NOT NULL,
    AVAILABLE VARCHAR(20) DEFAULT 'Yes',
    CHECK (AVAILABLE IN ('Yes', 'No', 'Middle', LPAD('a', 32767, 'a')))
);

-- materialized view sources
CREATE MATERIALIZED VIEW MAT_VIEW_4001
            BUILD IMMEDIATE
    REFRESH COMPLETE
AS
SELECT MAKER, MODEL, AVAILABLE
FROM CHECK_CONSTR_4001
WHERE AVAILABLE IN ('Yes', 'No', 'Middle',
                    '')
;

CREATE MATERIALIZED VIEW MAT_VIEW_SHORT
            BUILD IMMEDIATE
    REFRESH COMPLETE
AS
SELECT MAKER, MODEL, AVAILABLE
FROM CHECK_CONSTR_4001
WHERE AVAILABLE IN ('Yes', 'No', 'Middle')
;

-- views sources
CREATE VIEW VIEW_SHORT AS
SELECT MAKER, MODEL, TYPE, AVAILABLE
FROM CHECK_CONSTR_4001
WHERE AVAILABLE = 'No'
   OR AVAILABLE = 'Yes'
WITH CHECK OPTION
;


CREATE VIEW VIEW_4001 AS
SELECT MAKER, MODEL, TYPE, AVAILABLE
FROM CHECK_CONSTR_4001
WHERE AVAILABLE = 'No'
   OR AVAILABLE = 'Yes'
   OR AVAILABLE =
      ''
WITH CHECK OPTION
;

-- trigger sources (table)


CREATE TRIGGER TRIGGER_SHORT
    BEFORE INSERT OR UPDATE OF MAKER, MODEL
    ON CHECK_CONSTR_4001
    FOR EACH ROW
    WHEN (NEW.AVAILABLE <> 'Middle')
BEGIN
    INSERT INTO CHECK_CONSTR_4001(MAKER, MODEL, AVAILABLE)
    VALUES (:NEW.MAKER,
            :NEW.MODEL,
            'Yes');
END;


CREATE TRIGGER TRIGGER_4001
    BEFORE INSERT OR UPDATE OF MAKER, MODEL
    ON CHECK_CONSTR_4001
    FOR EACH ROW
    WHEN (NEW.AVAILABLE <> 'No')
BEGIN
    INSERT INTO CHECK_CONSTR_4001(MAKER, MODEL, AVAILABLE)
    VALUES (:NEW.MAKER,
            :NEW.MODEL,
            '');
END;

CREATE TRIGGER TRIGGER_VIEW_4001
    INSTEAD OF INSERT
    ON VIEW_4001

BEGIN
    INSERT INTO VIEW_4001
    VALUES (:NEW.MAKER, :NEW.MODEL, :NEW.TYPE,
            '');
END;